import React from "react"
import Card from "./subcomponents/cards.js"
import "../assets/style/producto.scss"
import Catalogo from "./catalogo"

class Frutas extends React.Component{
    constructor (props){
        super(props)
        this.state = {
            catalogo: [],
            isFetch: true,
        }
    }
    componentDidMount (){
        fetch("https://sheetdb.io/api/v1/lkcv6h24qpzi4")
            .then(response => response.json())
            .then(catalogoJson => this.setState({ catalogo: catalogoJson, isFetch: false}))
    }

    render(){
        if ( this.state.isFetch){
            return "loading..."
        }
        const frutas = this.state.catalogo.filter( frutas => {
            this.state.catalogo.tipo_de_producto = "fruta"
        })
        return (   
            <div className="container_productos">
            <Catalogo/>
            <h2>FRUTAS</h2>
                <div className="container_producto_items">
                    {this.state.frutas.map((fruta) => <Card src={fruta.src} nombre_producto={fruta.nombre} metodo_de_compra={fruta.metodo_de_compra} precio={fruta.precio}/>)}
                </div>
            </div>
        )
    }
}

export default Frutas
