import React, { useState } from "react"
import "../../assets/style/producto.scss"

function Card({ src, nombre_producto, metodo_de_compra, precio }){
    const [contador, setContador] = useState(0)
    const [totalCard, setTotalCard] = useState("")

    const incrementar = () => {
        setContador( contador + 1)
    }

    const disminuir = () => {
        if( contador === 0){
            setContador( contador )
        } else {
            setContador( contador - 1)
        }
    }

    const fijarTotalCard = () =>{
        setTotalCard( contador * precio)
    }

    return  (
        <div className="card">
            <div className="container_img_card">
                <img src={src} alt="slide-img"></img>
            </div>
            <div className="descripcion_card">
                <h4>{nombre_producto}</h4>
                <div className="informacion_precio_producto">
                    <p>{metodo_de_compra}</p>
                    <p>{precio}</p>
                </div>
                <div className="informacion_cantidad">
                    <p>Cantidad: </p>
                    <div className="informacion_botones">
                        <button onClick={disminuir, fijarTotalCard}>-</button><span>{contador}</span><button onClick={incrementar, fijarTotalCard}>+</button>
                    </div>
                </div>
                <div className="totalCard"><p>{totalCard}</p></div>
                <div className="button">
                    <button>AÃ±adir al carrito</button>
                </div>
            </div>
        </div>
    )
}

export default Card
