import React from "react"
import Card from "./subcomponents/cards.js"
import "../assets/style/producto.scss"
import Catalogo from "./catalogo"

class Frutas extends React.Component{
    constructor (props){
        super(props)
        this.state = {
            frutas: [],
            isFetch: true,
        }
    }
    componentDidMount (){
        fetch("https://sheetdb.io/api/v1/lkcv6h24qpzi4")
            .then(response => response.json())
            .then(frutasJson => this.setState({ frutas: frutasJson, isFetch: false}))
    }

    render(){
        if ( this.state.isFetch){
            return "loading..."
        }
        return (   
            <div className="container_productos">
            <Catalogo/>
            <h2>FRUTAS</h2>
                <div className="container_producto_items">
                    <Card src={imagenfruta} nombre_producto={nombrefruta} metodo_de_compra={comprafruta} precio={preciofruta}/>
                    {this.state.frutas.map((fruta) => <Card src={fruta.src} nombre_producto={nombre.fruta}/>)}
                </div>
            </div>
        )
    }
}

export default Frutas
